project_id = #{projectId,jdbcType=BIGINT}, meeting_type = #{meetingType,jdbcType=VARCHAR}, meeting_count = #{meetingCount,jdbcType=INTEGER}, meeting_date = #{meetingDate,jdbcType=TIMESTAMP}, status = #{status,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, updated_time = #{updatedTime,jdbcType=BIGINT}, created_time = #{createdTime,jdbcType=BIGINT},
