mapper.insert:
	    
		insert into sop_meeting_scheduling (id, project_id, meeting_type, 
	      meeting_count, meeting_date, status, 
	      remark, schedule_status,
    	  apply_time, reserve_time_start, reserve_time_end,last_time, updated_time, created_time
	      )
	    values (#{id,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{meetingType,jdbcType=VARCHAR}, 
	      #{meetingCount,jdbcType=INTEGER}, #{meetingDate,jdbcType=TIMESTAMP}, #{status,jdbcType=VARCHAR}, 
	      #{remark,jdbcType=VARCHAR}, #{scheduleStatus}, #{applyTime}, #{reserveTimeStart}, #{reserveTimeEnd},#{lastTime}, #{updatedTime,jdbcType=BIGINT}, #{createdTime,jdbcType=BIGINT}
	      )

mapper.update:
update sop_meeting_scheduling

     project_id = #{projectId,jdbcType=BIGINT}, 
	 meeting_type = #{meetingType,jdbcType=VARCHAR}, 
	 meeting_count = #{meetingCount,jdbcType=INTEGER}, 
	 meeting_date = #{meetingDate,jdbcType=TIMESTAMP}, 
	 status = #{status,jdbcType=VARCHAR}, 
	 remark = #{remark,jdbcType=VARCHAR}, 
	 schedule_status = #{scheduleStatus}, 
	 apply_time = #{applyTime}, 
	reserve_time_start = #{reserveTimeStart},
	reserve_time_end = #{reserveTimeEnd},
	 last_time = #{lastTime}, 
	 updated_time = #{updatedTime,jdbcType=BIGINT}, 
	 created_time = #{createdTime,jdbcType=BIGINT}, 


	
		 and id =#{id} 
		and project_id = #{projectId,jdbcType=BIGINT} 
		and meeting_type = #{meetingType,jdbcType=VARCHAR} 
		and meeting_count = #{meetingCount,jdbcType=INTEGER} 
		and meeting_date = #{meetingDate,jdbcType=TIMESTAMP} 
		and status = #{status,jdbcType=VARCHAR} 
		and remark = #{remark,jdbcType=VARCHAR} 
		
		and created_time = #{createdTime,jdbcType=BIGINT}

update sop_meeting_scheduling
meeting_count = #{meetingCount,jdbcType=INTEGER}, meeting_date = #{meetingDate,jdbcType=TIMESTAMP}, status = #{status,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, updated_time = #{updatedTime,jdbcType=BIGINT}, schedule_status = #{scheduleStatus}, apply_time = #{applyTime},
and id =#{id} and project_id = #{projectId,jdbcType=BIGINT} and meeting_type = #{meetingType,jdbcType=VARCHAR}

update sop_meeting_scheduling

	
	meeting_count = (CASE WHEN  meeting_count IS NULL THEN 1 ELSE  meeting_count+1  END),
	 
	 meeting_date = #{meetingDate,jdbcType=TIMESTAMP}, 
	 status = #{status,jdbcType=VARCHAR}, 
	 remark = #{remark,jdbcType=VARCHAR}, 
	 updated_time = #{updatedTime,jdbcType=BIGINT}, 
	 apply_time = #{applyTime}, 


	
		 and id =#{id}
		and project_id = #{projectId,jdbcType=BIGINT} 
        and meeting_type = #{meetingType,jdbcType=VARCHAR} 
        and meeting_count = #{meetingCount,jdbcType=INTEGER} 
        and remark = #{remark,jdbcType=VARCHAR} 
        and created_time = #{createdTime,jdbcType=BIGINT}
