mapper.select:
select count(1) as count,sum(project_valuations) as project_valuations
from sop_project

	   project_departId = #{projectDepartid,jdbcType=BIGINT} 
	   and create_uid = #{createUid,jdbcType=BIGINT} 
	   and created_time >= #{createDateFrom,jdbcType=BIGINT} 
	   and created_time >= #{createDateThrough,jdbcType=BIGINT}

select count(1)as count,project_progress from sop_project

	   project_departId = #{projectDepartid,jdbcType=BIGINT} 
	   and create_uid = #{createUid,jdbcType=BIGINT} 

group by project_progress

select id,project_code,project_name,project_departId,project_careerline,create_uname,create_uid from sop_project
    where id in
			
				#{item}

SELECT 
	id
FROM
	sop_project

	
		 
			CAST(REPLACE(project_progress,'projectProgress:','') AS SIGNED) > #{progressIntValue} 
		
		
		 
			AND id IN(
				SELECT 
					project_id
				FROM
					sop_task
				
					
						 AND project_id = #{projectId,jdbcType=BIGINT} 
						AND task_flag IN
						
							#{item}
						
						AND task_status  !=  #{notCompleteTaskStatus,jdbcType=VARCHAR}
					
				
				GROUP BY project_id
			)
		
		
		
		 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR}

select
	create_uid, project_departId, count(id) completed
from sop_project

	

		
			AND id IN
			
				#{item}
			
		

		
			AND create_uid IN
			
				#{item}
			
		
		
		 and project_type = #{projectType,jdbcType=VARCHAR} 

		 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
		
			AND project_departId IN
			
				#{item}
			
		

		 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 

		 and project_status = #{projectStatus,jdbcType=VARCHAR} 

		   and created_time >= #{startTime,jdbcType=BIGINT}  
		  and created_time <= #{endTime,jdbcType=BIGINT}  

		
		 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
	

GROUP BY create_uid
 order by ${sorting} 

	limit #{offset}, #{limit}

