select count(a.create_uid) from (
	select
		create_uid, project_departId, count(id) completed
	from sop_project
	
		
			
				AND id IN
				
					#{item}
				
			
	
			
				AND create_uid IN
				
					#{item}
				
			
			
			 and project_type = #{projectType,jdbcType=VARCHAR} 
	
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY create_uid
) a

select count(a.project_departId) from (
	select
	project_departId, count(id) completed
	from sop_project
	
		
			
			
				AND id IN
				
					#{item}
				
			
	
			
				AND create_uid IN
				
					#{item}
				
			
			
			 and project_type = #{projectType,jdbcType=VARCHAR} 
	
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY project_departId
) a

SELECT
	a.project_departId,a.completed completed,IFNULL(b.completed,0) type_completed
FROM 
	(
	SELECT
		project_departId, COUNT(id) completed
	FROM sop_project
	
		
	
			
				AND create_uid IN
				
					#{item}
				
			
			
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY project_departId
	) a
	left join
	(
	SELECT
		project_departId, COUNT(id) completed
	FROM sop_project
	
		
	
			
				AND create_uid IN
				
					#{item}
				
			
			
			 and project_type = #{projectType,jdbcType=VARCHAR} 
	
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY project_departId
	) b
	on 
		a.project_departId = b.project_departId 

	
	   and b.completed != #{resultNullFilter}
	

 order by ${sorting}

SELECT
	a.create_uid,a.completed completed,IFNULL(b.completed,0) type_completed
FROM 
	(
	SELECT
		create_uid, COUNT(id) completed
	FROM sop_project
	
		
	
			
				AND create_uid IN
				
					#{item}
				
			
			
	
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY create_uid
	) a
	left join
	(
	SELECT
		create_uid, COUNT(id) completed
	FROM sop_project
	
		
	
			
				AND create_uid IN
				
					#{item}
				
			
			
			 and project_type = #{projectType,jdbcType=VARCHAR} 
	
			 and project_departId = #{projectDepartid,jdbcType=BIGINT} 
			
				AND project_departId IN
				
					#{item}
				
			
	
			 and project_careerline = #{projectCareerline,jdbcType=VARCHAR} 
	
			 and project_status = #{projectStatus,jdbcType=VARCHAR} 
	
			   and created_time >= #{startTime,jdbcType=BIGINT}  
			  and created_time <= #{endTime,jdbcType=BIGINT}  
	
			
			 AND project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
		
	
	GROUP BY create_uid
	) b
	on
		a.create_uid = b.create_uid 

	
	   and b.completed != #{resultNullFilter}
	

 order by ${sorting}

	select
		pro.id, 
		pro.project_name, 
		pro.project_code, 
		pro.project_type, 
		pro.project_careerline, 
		pro.project_departId,
		pro.industry_own, 
    	pro.create_uid, 
    	pro.create_uname, 
    	pro.project_progress, 
    	pro.project_status, 
    	pro.updated_time, 
    	pro.created_time,
    	pro.user_portrait
	from sop_meeting_record meet,sop_project pro
	where
		meet.project_id = pro.id
		
		   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	
		and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	
		 and meet.record_type = #{recordType,jdbcType=TINYINT} 
		
		 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
		
		and pro.create_uid = #{createUid,jdbcType=BIGINT} 
		
			AND pro.create_uid IN
			
				#{item}
			
		
		
		 and pro.project_departId = #{departId,jdbcType=BIGINT} 
		
			AND pro.project_departId IN
			
				#{item}
			
		
		
		 AND pro.project_status != #{resultCloseFilter,jdbcType=VARCHAR} 
	GROUP BY pro.id
	 order by ${sorting}

SELECT s.`id`,s.`project_name`,s.project_company,s.`project_type`,s.`project_careerline`,s.`project_departId`, s.`finance_status`,p.ctime,p.grant_money ,k.health_state,s.`project_describe_financing`,s.`project_describe` FROM sop_project s INNER JOIN ( SELECT a.id,a.`part_grant_id`,a.`grant_money`, FROM_UNIXTIME(a.created_time/1000,'%Y-%m-%d %H:%i:%s') AS ctime,c.`project_id` FROM sop_grant_actual a LEFT JOIN sop_grant_part b ON a.`part_grant_id`=b.`id` LEFT JOIN `sop_grant_total` c ON b.`total_grant_id`=c.`id` and a.a.created_time >= #{startTime,jdbcType=TIMESTAMP} and a.created_time <= #{endTime,jdbcType=TIMESTAMP} GROUP BY c.`project_id` ORDER BY a.created_time ASC ) p ON s.id=p.project_id LEFT JOIN ( SELECT FROM_UNIXTIME(v.created_time/1000,'%Y-%m-%d %H:%i:%s') ,v.project_id,v.health_state FROM ( SELECT * FROM sop_project_health q ORDER BY q.`created_time` DESC) v GROUP BY v.`project_id` ) k ON p.project_id=k.project_id
s.project_departId = #{departId}
order by ${sorting}

SELECT count(*) FROM sop_project s INNER JOIN ( SELECT a.id,a.`part_grant_id`,a.`grant_money`, FROM_UNIXTIME(a.created_time/1000,'%Y-%m-%d %H:%i:%s') AS ctime,c.`project_id` FROM sop_grant_actual a LEFT JOIN sop_grant_part b ON a.`part_grant_id`=b.`id` LEFT JOIN `sop_grant_total` c ON b.`total_grant_id`=c.`id` and a.a.created_time >= #{startTime,jdbcType=TIMESTAMP} and a.created_time <= #{endTime,jdbcType=TIMESTAMP} GROUP BY c.`project_id` ORDER BY a.created_time ASC ) p ON s.id=p.project_id LEFT JOIN ( SELECT FROM_UNIXTIME(v.created_time/1000,'%Y-%m-%d %H:%i:%s') ,v.project_id,v.health_state FROM ( SELECT * FROM sop_project_health q ORDER BY q.`created_time` DESC) v GROUP BY v.`project_id` ) k ON p.project_id=k.project_id
s.project_departId = #{departId}
order by ${sorting}

SELECT s.`id`,s.`project_name`,s.project_company,s.`project_type`,s.`project_careerline`,s.`project_departId`, s.`finance_status`,p.ctime,p.grant_money ,k.health_state,s.`project_describe_financing`,s.`project_describe` FROM sop_project s INNER JOIN ( SELECT c.id,a.`grant_name`,c.`grant_money`,FROM_UNIXTIME(c.created_time/1000,'%Y-%m-%d') AS ctime ,a.`project_id` FROM sop_grant_total a LEFT JOIN sop_grant_part b ON a.`id`=b.`total_grant_id` LEFT JOIN `sop_grant_actual` c ON b.`id`=c.`part_grant_id` GROUP BY a.`project_id` ORDER BY c.created_time ASC ) p ON s.id=p.project_id LEFT JOIN ( SELECT FROM_UNIXTIME(v.created_time/1000,'%Y-%m-%d %H:%i:%s') ,v.project_id,v.health_state FROM ( SELECT * FROM sop_project_health q ORDER BY q.`created_time` DESC) v GROUP BY v.`project_id` ) k ON p.project_id=k.project_id
s.project_departId = #{departId}
order by ${sorting}
