			        and id =#{id}
			    and project_id = #{projectId,jdbcType=BIGINT} 
			    and before_uid = #{beforeUid,jdbcType=BIGINT} 
			    and before_department_id = #{beforeDepartmentId,jdbcType=BIGINT} 
