			    and after_department_id = #{afterDepartmentId,jdbcType=BIGINT} 
		        and record_status = #{recordStatus} 		
		        and record_status is null

update sop_transfer_record
id =#{id}, project_id = #{projectId,jdbcType=BIGINT}, before_uid = #{beforeUid,jdbcType=BIGINT}, before_department_id = #{beforeDepartmentId,jdbcType=BIGINT}, after_uid = #{afterUid,jdbcType=BIGINT}, after_department_id = #{afterDepartmentId,jdbcType=BIGINT}, transfer_reason = #{transferReason,jdbcType=VARCHAR}, record_status = #{recordStatus}, undo_reason = #{undoReason,jdbcType=VARCHAR}, refuse_reason = #{refuseReason,jdbcType=VARCHAR}, updated_time = #{updatedTime,jdbcType=BIGINT}, created_time = #{createdTime,jdbcType=BIGINT}
