SELECT sp.finance_status as fanceStatus,COUNT(si.id) as countVisit FROM schedule_info si,sop_project sp
AND si.created_id IN
#{item}
and si.type = 2 and si.is_del = 0 and si.project_id != '' and si.project_type = 1
and si.start_time >= #{startTime,jdbcType=TIMESTAMP}
and si.start_time <= #{endTime,jdbcType=TIMESTAMP}
and si.project_id = sp.id
GROUP BY sp.finance_status ORDER BY SUBSTR(sp.finance_status,15)+0 ASC

select count(id) from schedule_info
 
	
	    
			AND created_id IN
			
				#{item}
			
	     
	        and type = 2
	        and is_del = 0
	    
         and project_type = #{isProject}
	     
	     
	        and project_type = #{isProject}
	     
	     
	        and start_time >= #{startTime,jdbcType=TIMESTAMP}
	     
	     
	        and start_time <= #{endTime,jdbcType=TIMESTAMP}
	     
	     and start_time <= NOW()

select COUNT(si.id) from schedule_info si,sop_interview_record sr
 
	
	    
			AND si.created_id IN
			
				#{item}
			
	     
	        and si.type = 2
	        and si.is_del = 0
	    
	        and project_type = #{isProject}
	     
	     
	        and project_type = #{isProject}
	     
	     
	        and si.start_time >= #{startTime,jdbcType=TIMESTAMP}
	     
	     
	        and si.start_time <= #{endTime,jdbcType=TIMESTAMP}
	     
	     and si.start_time <= NOW()
	     and sr.schedule_id = si.id

mapper.sql:
	select start_time
	
	,
	DATE_SUB(start_time,INTERVAL WEEKDAY(start_time) DAY ) week_start,
	DATE_ADD(start_time,INTERVAL 6-WEEKDAY(start_time) DAY ) week_end
	
	from schedule_info
	
	
		and start_time >= #{startTimeFrom,jdbcType=TIMESTAMP}
	
	
		and start_time <= #{startTimeThrough,jdbcType=TIMESTAMP}
	
	
		and exists (select id from sop_interview_record where schedule_id = schedule_info.id)
	
	
        and project_type = #{isProject}
     
     
        and project_type = #{isProject}

