mapper.select:
SELECT DISTINCT CASE WHEN g.sub_id IS NOT NULL THEN CONCAT(r.id,'-',g.sub_id) ELSE r.id END AS pk,r.id AS relate_id,r.code,r.parent_id,r.report_type,g.is_score,g.score_max,g.sub_id,a.process_mode,a.dict_id,a.grade

,rt.id AS value_id,rt.content_choose AS VALUE, rg.id AS score_id, CASE WHEN g.sub_id = 2 THEN rg.grade2 ELSE rg.grade1 END AS score

FROM information_title_relate r
LEFT JOIN information_grade g ON r.id = g.title_relate_id
LEFT JOIN information_grade_auto a ON a.title_relate_id = r.id

LEFT JOIN information_result rt ON rt.title_id = r.title_id
LEFT JOIN (SELECT * FROM information_result_grade WHERE project_id = #{projectId}) rg ON rg.title_relate_id = r.id



	 and r.id =#{relateId}
	 and r.parent_id =#{parentId}
	 and r.report_type =#{reportType}
	and rt.project_id =#{projectId}
	AND r.code LIKE CONCAT(#{code},'%')

			and r.id in 

			#{item}
