	     and finance_unit = #{financeUnit,jdbcType=VARCHAR} 
	     and finance_status = #{financeStatus,jdbcType=VARCHAR} 
	     and updated_uid = #{createUid,jdbcType=LONG} 
	     and updated_time = #{createdTime,jdbcType=LONG} 
	


order by finance_date desc

	limit #{offset}, #{limit}

